public class LogicalOperation1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean p = true;
		boolean negation = !p;
		System.out.println("Logical negation of p being " + p + " is " + negation);
		p = false;
		negation = !p;
		System.out.println("Logical negation of p being " + p + " is " + negation);
	}

}
